import styled, { css, createGlobalStyle } from 'styled-components';

import { hideText, normalizeButton, normalizeInput } from './utils/styles';
import { whatsappGreenColor, whatsappGreenDarkColor } from './utils/colors';
import { zIndex } from './utils/z-index';

const buttonSize = '44px';

const GlobalStyles = createGlobalStyle`
  *,
  *::before,
  *::after {
    box-sizing: inherit;
  }

  html {
    font-family: sans-serif;
    box-sizing: border-box;
    margin: 0;
    padding: 0;
  }

  body {
    margin: 0;
    padding: 0;
    color: #333;
  }

  a {
    text-decoration: none;
    color: ${whatsappGreenColor};

    &:visited {
      color: ${whatsappGreenDarkColor};
    }
  }

  button {
    cursor: pointer;
  }

  html,
  body,
  #root {
    height: 100%;
  }

  .rootContainer {
    height: 100%;
    background-image: linear-gradient(159deg, #1D1E1F 0%, #151617 77%) !important;
    background-image: url(https://www.sketchapp.com/images/pages/home/52/random-shapes.svg);
    background-repeat: repeat;
    background-position: center;
    opacity: 1;
    background-color: #000;
  }

  media {
    display: block;
    min-height: 64px;
    min-width: 64px;
    background-color: red;

  }

  .relative {
    position: relative;
  }
  .absolute {
    position: absolute;
  }

  .box-shadow {
    box-shadow: 0 3px 6px 0 rgba(0,0,0,.3);
  }

  .header {
    background: #2D384A;
    background: transparent;
    height: 252px;
  }
  .header .top-header {
    background: #1F2733;
    background: transparent;
    height: 64px;
    width: 100%;
    font-size: 24px;
    line-height: 64px;
  }
  .container {
    margin-top: -64px;
    z-index: 10;
  }

  .container .card {
    max-width: 992px;
    background: #ffffff;
    margin: 0 auto;
  }
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100%;
`;

const MenuOpenButton = styled.button`
  ${normalizeButton}
  ${hideText}

  position: fixed;
  width: ${buttonSize};
  height: ${buttonSize};
  left: 1rem;
  bottom: 1rem;
  border-radius: 50%;
  background-color: ${whatsappGreenColor};

  &::after {
    content: '';
    display: block;
    position: absolute;
    width: 16px;
    height: 2px;
    top: 50%;
    left: 50%;
    transform: translate3d(-50%, -50%, 0);
    background-color: white;
    box-shadow: 0 -5px 0 white, 0 5px 0 white;
  }

  @media (min-width: 700px) {
    left: 2rem;
    bottom: 2rem;
  }
`;

const MenuCloseButton = styled.button`
  ${normalizeButton}
  ${hideText}

  position: absolute;
  width: ${buttonSize};
  height: ${buttonSize};
  top: 0;
  right: 0;
  background-color: transparent;
  opacity: 0.5;
  transition: opacity 0.3s ease;

  &:hover,
  &:focus {
    opacity: 1;
  }

  &::before,
  &::after {
    content: '';
    display: block;
    position: absolute;
    width: 20px;
    height: 2px;
    top: 50%;
    left: 50%;
    transform-origin: 50% 50%;
    background-color: black;
  }

  &::before {
    transform: translate3d(-50%, -50%, 0) rotate(45deg);
  }

  &::after {
    transform: translate3d(-50%, -50%, 0) rotate(135deg);
  }
`;

const Overlay = styled.button`
  ${normalizeButton}

  display: block;
  position: fixed;
  width: 100%;
  top: 0;
  bottom: 0;
  background-color: black;
  opacity: ${props => (props.isActive ? 0.2 : 0)};
  transition: opacity 0.3s ease;
  z-index: ${zIndex.overlay};
  ${props =>
    !props.isActive &&
    css`
      pointer-events: none;
    `}
`;

const Sidebar = styled.aside`
  position: fixed;
  width: 280px;
  top: 0;
  left: 0;
  bottom: 0;
  background-color: white;
  transform: translate3d(${props => (props.isOpen ? 0 : '-100%')}, 0, 0);
  transition: transform 0.3s ease;
  z-index: ${zIndex.sidebar};
`;

const SidebarContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  position: absolute;
  top: ${buttonSize};
  left: 0;
  bottom: 0;
  right: 0;
  padding: 1rem;
  border-top: 1px solid #eee;
`;

const Field = styled.div`
  * + * {
    margin-top: 0.375rem;
  }
`;

const Label = styled.label`
  display: block;
  opacity: 0.8;
`;

const Input = styled.input`
  ${normalizeInput}

  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: 4px;
  width: 100%;
  height: 1.8rem;
  padding: 0 0.3rem;
  background-color: #fafafa;
  box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.07);
`;

const InputDescription = styled.div`
  font-size: 80%;
  opacity: 0.6;
`;

const Header = styled.header`
  padding: 10px;
  display: flex;
  align-items: center;

  > *:first-child {
    flex: 1 1 auto;
  }

  @media (max-width: 699px) {
    flex-direction: column;

    > * + * {
      margin-top: 0.5rem;
    }
  }

  @media (min-width: 700px) {
    > * + * {
      margin-left: 1rem;
    }
  }
`;

export {
  GlobalStyles,
  Container,
  MenuOpenButton,
  MenuCloseButton,
  Overlay,
  Sidebar,
  SidebarContainer,
  Field,
  Label,
  Input,
  InputDescription,
  Header,
};
